import React, { Component } from 'react';

export default class Short_link extends Component {
    constructor(props){
        super(props)
        this.state={
            input_value: '',
            data:[]
        }
    }
    getUserURL =(e)=>{
        this.setState({
            input_value: e.target.value
        })
        // console.log(this.input_value)
    }
    getShortURL=(e)=>{
        e.preventDefault();
        fetch(`https://api.shrtco.de/v2/shorten?url=${this.state.input_value}`)
         .then((res)=>res.json())
         .then((response_data) => {
            this.setState({
                data:[response_data.result]

            });

    })
   }
    render() {
        console.log(this.state)
        return (
            <div className="main">
                <div className='input_field'>
                    <form onSubmit={this.getShortURL}>
                    <input type="text" onChange={this.getUserURL}/>
                    <button type="submit" >Get</button>
                    <span>Short_URl:{}</span>
                    </form>
                </div>

            </div>
        )
    }
}
