import React, { Component } from 'react';
import "../components/style/input_feild.scss";

export default class Input_feild extends Component {
    constructor(props) {
        super(props);
        this.state = {
          input_value: "",
          status: false,
          copy_status: "copy",
          fetchingStatus: "SHORTEN",
          resultData: [],
          errorMsg: ""
        };
      }
      getUserURL = (e) => {
        this.setState({
          input_value: e.target.value,
          fetchingStatus: "SHORTEN",
        });

        console.log(this.state.input_value)
      };

      getShortURL = async(e) => {
        this.setState({ fetchingStatus: "fetching.." });
        e.preventDefault();
        if (this.state.input_value.length == 0) {
          alert("Enter the url");
        } else {
          try {

             await fetch(
                `https://api.shrtco.de/v2/shorten?url=${this.state.input_value}`
              )
                .then((res) => res.json())
                .then((response_data) => {
                  if(!response_data.ok){
                    throw Error(response_data.error),
                    this.setState({errorMsg: response_data.error,fetchingStatus: "SHORTEN"})
                  }else{
                  this.setState({
                    status: true,
                    fetchingStatus: "fetched",
                    resultData: [...this.state.resultData,{url:response_data.result.short_link,id:new Date().getTime()}],

                  });
                }
                });
              localStorage.setItem("key",JSON.stringify(this.state.resultData))
              this.getDta();
          } catch (e) {
            console.log(e);
            this.setState({
              resultData: "",
              status: false,
            });
          }
        }
      };
      getDta = ()=>{
          let list=JSON.parse("key");
          console.log(list);
          // if(list){
          //     this.setState({
          //         resultData: [...this.state.resultData,list]
          //     })
          // }else {
          //     return [];
          // }
      }
    render() {
        console.log(this.state)
        return (
           <div className="input_field">
               <div className="input_field_inner">
                   <form onSubmit={this.getShortURL}>

                   <div className="input_url">
                      <label For="input_url">

                          <input type="text" placeholder="Shorten a link here..." onChange={this.getUserURL} /></label></div>

                      <div className="input_submit">  <label For="input_submit"><input type="submit" value={this.state.fetchingStatus} /></label></div>

                      </form>
               </div>
               <div className="short_urls">
                   <ul>
                       {/* {this.state.resultData.map((e,i)=>{
                            return(
                                <li key={i}>{e.url}</li>
                            ) */}

    })}
                   </ul>
               </div>
           </div>
        )
    }
}
