import React, { Component } from 'react';
import '../components/short_link.scss'
export default class Short_link extends Component {
    constructor(props){
        super(props)
        this.state={
            input_value: '',
            data:[],
            status: false
        }
    }
    getUserURL =(e)=>{
        this.setState({
            input_value: e.target.value
        })
        // console.log(this.input_value)
    }
    getShortURL=(e)=>{
        e.preventDefault();
        try{
        fetch(`https://api.shrtco.de/v2/shorten?url=${this.state.input_value}`)
         .then((res)=>res.json())
         .then((response_data) => {
            this.setState({
                data:response_data.result,
                status: true

            });

    })}
    catch(console){
        this.setState({
            data:'',
            status:false

        });
    }
   }
   copy=() => {
       setTimeout(() => {
           cons
       }, 3000);
       navigator.clipboard.writeText(this.state.data.short_link)
    }
    render() {
        console.log(this.state)
        return (
               <div className="container">
                <div className="main">

                    <form onSubmit={this.getShortURL}>
                   <span><label>USER_URL:</label>
                    <input type="text" onChange={this.getUserURL}/></span>



                    <span className="result_url"><label>Short_URL:</label>
                    <input type="text" value={this.state.data.short_link} />
                       <span className="copy"> <button onClick={this.copy} >copy</button></span>
                    </span>

                    <button type="submit" className='submit' >Shorten</button>
                    </form>


            </div>
            </div>
        )
    }
}
