import React, { Component } from 'react';
import '../components/short_link.scss'
export default class Short_link extends Component {
    constructor(props){
        super(props)
        this.state={
            input_value: '',
            data:[],
            status: false,
            copy_status: "copy",
            fatchingStatus:"SHORTEN"
        }
    }
    getUserURL =(e)=>{
        this.setState({
            input_value: e.target.value
        })
        // console.log(this.input_value)
    }
    getShortURL=(e)=>{
        this.setState({fatchingStatus:"fetching.."})
        e.preventDefault();
        if(this.state.input_value.length==0){
            alert("Enter the url")
        }else{
        try{
        fetch(`https://api.shrtco.de/v2/shorten?url=${this.state.input_value}`)
        .then((res)=>res.json())
        .then((response_data) => { 
            this.setState({
                data:response_data.result,
                status: true,
                fatchingStatus: "fetched"


            });

    })}
    catch(console){
        this.setState({
            data:'',
            status:false,

        });
    }
}
   }
   copy=() => {

       this.setState({copy_status:"copied"})
       navigator.clipboard.writeText(this.state.data.short_link);


    }
    render() {
        console.log(this.state.cp)
        return (
               <div className="container">
                <div className="main">

                    <form onSubmit={this.getShortURL}>
                   <span><label>USER_URL:</label>
                    <input type="text" onChange={this.getUserURL}/></span>



                    <span className="result_url"><label>Short_URL:</label>
                    <input type="text" value={this.state.data.short_link} />

                       <span className="copy"> <input className={this.state.copy_status=="copied"?("button button-btn"):("button")} type="button" onClick={this.copy} value={this.state.copy_status} /></span>
                    </span>

                    <input type="submit" className='submit' value={this.state.fatchingStatus}/>
                    </form>


            </div>
            </div>
        )
    }
}
