import React, { Component } from "react";
import Loader from "react-loader-spinner";
import "../components/short_link.scss";
export default class Short_link extends Component {
  constructor(props) {
    super(props);
    this.state = {
      input_value: "",
      data: [],
      status: false,
      copy_status: "copy",
      fetchingStatus: "SHORTEN",
    };
  }
  getUserURL = (e) => {
    this.setState({
      input_value: e.target.value,
      fetchingStatus: "SHORTEN",
    });
    // console.log(this.input_value)
  };

  getShortURL = (e) => {
    this.setState({ fetchingStatus: "fetching.." });
    e.preventDefault();
    if (this.state.input_value.length == 0) {
      alert("Enter the url");
    } else {
      try {
        setTimeout(() => {
          fetch(
            `https://api.shrtco.de/v2/shorten?url=${this.state.input_value}`
          )
            .then((res) => res.json())
            .then((response_data) => {
              this.setState({
                data: response_data.result,
                status: true,
                fetchingStatus: "fetched",
              });
            });
        }, 3000);
      } catch (console) {
        this.setState({
          data: "",
          status: false,
        });
      }
    }
  };
  copy = () => {
    if (this.state.data.short_link.length !== 0) {
      this.setState({ copy_status: "copied" });
      navigator.clipboard.writeText(this.state.data.short_link);
    } else {
      this.setState({ copy_status: "copy" });
    }
  };

  render() {
    console.log(this.state.status);
    return (
      <div className="container">
        <div className="title">
          <div className="url">url</div>
          <div className="shortern">shortern</div>
        </div>
        <div className="main">
          <form onSubmit={this.getShortURL}>
            <span>
              <label>USER_URL:</label>
              <input type="text" onChange={this.getUserURL} />
            </span>

            {!this.state.status ? (
              <div className="loader">
                <Loader
                  type="ThreeDots"
                  color="#00BFFF"
                  height={80}
                  width={80}
                />
              </div>
            ) : (
              <span className="result_url">
                <label>Short_URL:</label>
                <input type="text" value={this.state.data.short_link} />
                <span className="copy">
                  {" "}
                  <input
                    className={
                      this.state.copy_status == "copied"
                        ? "button button-btn"
                        : "button"
                    }
                    type="button"
                    onClick={this.copy}
                    value={this.state.copy_status}
                  />
                </span>
              </span>
            )}

            <input
              type="submit"
              className={
                this.state.fetchingStatus == "fetching.."
                  ? "submit submit-sub"
                  : "submit"
              }
              value={this.state.fetchingStatus}
            />
          </form>
        </div>
      </div>
    );
  }
}
