import React, { Component } from 'react';

export default class Short_link extends Component {
    constructor(props){
        super(props)
        this.state={
            input_value: '',
            data:[]
        }
    }
    getUserURL =(e)=>{
        this.setState({
            input_value: e.target.value
        })
        // console.log(this.input_value)
    }
    getShortURL=(e)=>{
        e.preventDefault();
        fetch(`https://api.shrtco.de/v2/shorten?url=${this.state.inp}`)
    }
    render() {
        console.log(this.state.input_value)
        return (
            <div className="main">
                <div className='input_field'>
                    <form onSubmit={this.getShortURL}>
                    <input type="text" onChange={this.getUserURL}/>
                    </form>
                </div>

            </div>
        )
    }
}
