import React, { Component } from 'react';
import '../components/short_link.scss'
export default class Short_link extends Component {
    constructor(props){
        super(props)
        this.state={
            input_value: '',
            data:[],
            status: false
        }
    }
    getUserURL =(e)=>{
        this.setState({
            input_value: e.target.value
        })
        // console.log(this.input_value)
    }
    getShortURL=(e)=>{
        e.preventDefault();
        try{
        fetch(`https://api.shrtco.de/v2/shorten?url=${this.state.input_value}`)
         .then((res)=>res.json())
         .then((response_data) => {
            this.setState({
                data:response_data.result,
                status: true

            });

    })}
    catch(console){
        this.setState({
            data:'',
            status:false

        });
    }
   }
    render() {
        console.log(this.state)
        return (
               <div className="container">
                <div className="main">
                <div className='input_field'>
                    <form onSubmit={this.getShortURL}>
                    <label>U_URl:</label>
                    <input type="text" onChange={this.getUserURL}/>
                    <button type="submit" >Shorten</button>

                    </form>
                    <span><label>Short_URl:</label>
                    <input type="text" value={this.state.data.short_link} /></span>
                </div>

            </div>
            </div>
        )
    }
}
