import React, { Component } from 'react';
import "../components/style/input_feild.scss";

export default class Input_feild extends Component {
    constructor(props) {
        super(props);
        this.state = {
          input_value: "",
          status: false,
          copy_status: "copy",
          fetchingStatus: "SHORTEN",
          resultData: "",
          errorMsg: ""
        };
      }
      getUserURL = (e) => {
        this.setState({
          input_value: e.target.value,
          fetchingStatus: "SHORTEN",
        });

        console.log(this.state.input_value)
      };

    render() {
        return (
           <div className="input_field">
               <div className="input_field_inner">
                   <form onSubmit={this.getShortURL}>

                   <div className="input_url">
                      <label For="input_url">

                          <input type="text" placeholder="Shorten a link here..." onChange={this.getUserURL} /></label></div>

                      <div className="input_submit">  <label For="input_submit"><input type="submit" value="Shorten!" /></label></div>

                      </form>
               </div>
           </div>
        )
    }
}
